const fs = require(`fs`);

/**
 * Determine if a file exists at the specified path
 * @param {string} filepath
 * @returns {Boolean}
 */
module.exports = function exists(filepath) {
  try {
    fs.statSync(filepath);
    return true;
  }
  catch (err) {
    return false;
  }
};
