import {assert} from '@ciscospark/test-helper-chai';
import {nodeOnly, browserOnly} from '@ciscospark/test-helper-mocha';
import {inBrowser} from '@ciscospark/common';

describe(`common`, () => {
  describe(`inBrowser`, () => {
    browserOnly(it)(`is true in browsers`, () => assert.isTrue(inBrowser));
    nodeOnly(it)(`is false in node`, () => assert.isFalse(inBrowser));
  });
});
