import RoapMessage from './roap-message';

/**
 * An OFFER_RESPONSE message
 */
export default class OfferResponseMessage extends RoapMessage {
  messageType = 'OFFER_RESPONSE';

  sdps = [];

  /**
   * constructor
   * @param {number} seq
   * @param {Array<string>} sdps
   */
  constructor(seq, sdps) {
    super(seq);
    if (sdps) {
      this.sdps = sdps;
    }
  }

  /**
   * Indicates if this message is valid according to its
   * {@link RoapMessage#messageType}
   * @see {@link RoapMessage#isValid()}
   * @returns {boolean}
   */
  isValid() {
    return Boolean(this.version === 2 && this.messageType === 'OFFER_RESPONSE' && this.sdps && this.sdps.length);
  }
}
