import '../..';

import {assert} from '@ciscospark/test-helper-chai';
import retry from '@ciscospark/test-helper-retry';
import testUsers from '@ciscospark/test-helper-test-users';
import {generateRandomString} from '@ciscospark/test-users-legacy';
import CiscoSpark from '@ciscospark/spark-core';
import uuid from 'uuid';

describe(`plugin-lyra`, () => {
  describe(`Device`, () => {
    let participants;
    let lyraMachine;
    let spock;
    let conversation;

    before(`create lyra machine`, function() {
      this.timeout(retry.timeout(20000));
      return retry(() => testUsers.create({
        count: 1,
        config: {
          machineType: `LYRA_SPACE`,
          type: `MACHINE`,
          password: `${generateRandomString(32)}d_wA*`
        }
      }))
        .then((machines) => {
          lyraMachine = machines[0];
          lyraMachine.spark = new CiscoSpark({
            credentials: {
              authorization: lyraMachine.token
            }
          });

        // binding to conversation only works with spark board device
          lyraMachine.spark.internal.device.config.defaults.deviceType = `SPARK_BOARD`;
          lyraMachine.spark.internal.device.config.defaults.model = `SparkBoard Test`;
          lyraMachine.spark.internal.device.config.defaults.localizedModel = `SparkJSSDKTest`;
          lyraMachine.spark.internal.device.config.defaults.systemVersion = `SparkJSSDKTest`;
          lyraMachine.spark.internal.device.config.defaults.systemName = `Darling`;

          return lyraMachine.spark.internal.mercury.connect();
        })
        .then(() => lyraMachine.spark.internal.lyra.space.get({id: lyraMachine.id}))
        .then((space) => {
          lyraMachine.space = space;
        });
    });

    before(`create users`, () => testUsers.create({count: 2})
      .then((users) => {
        participants = users;
        spock = participants[0];

        return Promise.all(Array.map(participants, (participant) => {
          participant.spark = new CiscoSpark({
            credentials: {
              authorization: participant.token
            }
          });

          return participant.spark.internal.mercury.connect();
        }));
      }));

    before(`create conversation`, () => retry(() => participants[0].spark.internal.conversation.create({
      displayName: `Test Lyra Conversation`,
      participants
    }))
      .then((c) => {
        conversation = c;
        return conversation;
      }));

    describe(`#getAudioState`, () => {
      let audioState;

      before(`put audio state`, () => {
        audioState = {
          volume: {
            level: 2
          },
          microphones: {
            muted: false
          },
          deviceUrl: lyraMachine.spark.internal.device.url
        };

        return lyraMachine.spark.internal.lyra.device.putAudioState(lyraMachine.space, audioState);
      });

      it(`returns audio state`, () => lyraMachine.spark.internal.lyra.device.getAudioState(lyraMachine.space)
        .then((res) => {
          assert.equal(res.microphones.muted, audioState.microphones.muted);
          assert.equal(res.volume.level, audioState.volume.level);
        }));
    });

    describe(`when a call is in progress`, () => {
      before(`ensure participant joined space`, () => spock.spark.internal.lyra.space.join(lyraMachine.space)
        .then(() => lyraMachine.spark.internal.lyra.space.verifyOccupant(lyraMachine.space, spock.id))
        .then(() => spock.spark.internal.lyra.space.bindConversation(lyraMachine.space, conversation)));

      before(`make a call`, () => {
        const locus = {
          url: conversation.locusUrl,
          correlationId: uuid.v4()
        };

        return spock.spark.request({
          method: `POST`,
          uri: `${locus.url}/participant`,
          body: {
            correlationId: locus.correlationId,
            deviceUrl: spock.spark.internal.device.url,
            localMedias: []
          }
        });
      });

      after(`remove binding`, () => spock.spark.internal.lyra.space.unbindConversation(lyraMachine.space, conversation)
        // After hooks shouldn't be able to break tests
        .catch((err) => console.error(err)));

      it(`mutes`, () => {
        spock.spark.internal.lyra.device.mute(lyraMachine.space);
        return lyraMachine.spark.internal.mercury.when(`event:lyra.space_audio_microphones_mute_action`)
          .then(([event]) => {
            assert.equal(event.data.action, `mute`);
          });
      });

      it(`unmutes`, () => {
        spock.spark.internal.lyra.device.unmute(lyraMachine.space);
        return lyraMachine.spark.internal.mercury.when(`event:lyra.space_audio_microphones_mute_action`)
          .then(([event]) => {
            assert.equal(event.data.action, `unMute`);
          });
      });

      it(`increases volume`, () => {
        spock.spark.internal.lyra.device.increaseVolume(lyraMachine.space);
        return lyraMachine.spark.internal.mercury.when(`event:lyra.space_audio_volume_change_action`)
          .then(([event]) => {
            assert.equal(event.data.action, `increase`);
          });
      });

      it(`decreases volume`, () => {
        spock.spark.internal.lyra.device.decreaseVolume(lyraMachine.space);
        return lyraMachine.spark.internal.mercury.when(`event:lyra.space_audio_volume_change_action`)
          .then(([event]) => {
            assert.equal(event.data.action, `decrease`);
          });
      });

      it(`sets volume`, () => {
        spock.spark.internal.lyra.device.setVolume(lyraMachine.space, 2);
        return lyraMachine.spark.internal.mercury.when(`event:lyra.space_audio_volume_set_action`)
          .then(([event]) => {
            assert.equal(event.data.level, 2);
          });
      });
    });
  });
});
