import '@ciscospark/internal-plugin-presence';

import {assert} from '@ciscospark/test-helper-chai';
import CiscoSpark from '@ciscospark/spark-core';
import '@ciscospark/internal-plugin-presence';
import testUsers from '@ciscospark/test-helper-test-users';

describe(`plugin-presence`, function() {
  this.timeout(60000);
  describe(`Presence`, () => {
    let mccoy, spock;

    beforeEach(`create users`, () => testUsers.create({count: 1})
      .then((users) => {
        [spock, mccoy] = users;
        spock.spark = new CiscoSpark({
          credentials: {
            authorization: users[0].token
          }
        });
        mccoy.spark = new CiscoSpark({
          credentials: {
            authorization: users[1].token
          }
        });
      }));

    describe(`#get()`, () => {
      it(`gets a person's status by id`, () => {
        return spock.spark.presence.get(mccoy.id)
          .then((presenceResponse) => {
            assert.property(presenceResponse, mccoy.id);
          });
      });
    });

    describe(`#list()`, () => {
      it(`returns a list of people matching email address`, () => {
        return spock.spark.presence.list([mccoy.id, spock.id])
          .then((presenceResponse) => {
            assert.isAbove(Object.keys(presenceResponse).length, 0);
            assert.property(presenceResponse, mccoy.id);
            assert.property(presenceResponse, spock.id);
          });
      });
    });

    describe(`#subscribe`, () => {
      it(`subscribes to and returns current status of a person id`, () => spock.spark.presence.subscribe(mccoy.id)
        .then((presenceResponse) => assert.isDefined(presenceResponse[mccoy.id]))
      );
    });

    describe(`#subscribeList`, () => {
      it(`subscribes and returns current status of a list of people ids`, () =>
        spock.spark.presence.subscribeList([mccoy.id, spock.id])
          .then((presenceResponse) => {
            assert.isAbove(Object.keys(presenceResponse).length, 0);
            assert.property(presenceResponse, mccoy.id);
            assert.property(presenceResponse, spock.id);
          })
      );
    });

    describe(`#unsubscribe`, () => {
      it(`unsubscribes to presence updates of a single person id`, () =>
        spock.spark.presence.unsubscribe(mccoy.id)
          .then((res) => assert.statusCode(res, 200))
      );
    });

    describe(`#unsubscribeList`, () => {
      it(`unsubscribes to presence updates of a list of people ids`, () =>
        spock.spark.presence.unsubscribeList([mccoy.id, spock.id])
          .then((res) => assert.statusCode(res, 200))
      );
    });

    describe(`#setStatus`, () => {
      it(`sets the presence status of the current user`, () =>
        spock.spark.presence.setStatus(`dnd`, 1500)
          .then((statusResponse) => {
            assert.statusCode(statusResponse, 200);
            assert.property(statusResponse, spock.id);
          })
      );
    });
  });
});
