import {assert} from '@ciscospark/test-helper-chai';
import MockSpark from '@ciscospark/test-helper-mock-spark';
import sinon from '@ciscospark/test-helper-sinon';
import Device from '@ciscospark/internal-plugin-wdm';
import deviceFixture from '../lib/device-fixture';

describe(`plugin-wdm`, () => {
  describe(`Device`, () => {
    let device;

    beforeEach(() => {
      const spark = new MockSpark({
        children: {
          device: Device
        }
      });
      spark.internal.device.set(deviceFixture);

      device = spark.internal.device;

      device.config.preDiscoveryServices = {};
    });

    describe(`#getWebSocketUrl()`, () => {
      it(`returns web socket url based on the availability of mercuryConnection host`, () => {
        return assert.becomes(device.getWebSocketUrl(), `wss://mercury-connection.a6.ciscospark.com/v1/apps/wx2/registrations/WEBSOCKETID/messages`);
      });

      it(`returns updated web socket url when host changes`, () => {
        return device.markUrlFailedAndGetNew(`wss://mercury-connection.a6.ciscospark.com/v1/apps/wx2/registrations/WEBSOCKETID/messages`)
          .then(() => assert.becomes(device.getWebSocketUrl(), `wss://mercury-connection-a5.wbx2.com/v1/apps/wx2/registrations/WEBSOCKETID/messages`));
      });
    });

    describe(`#useServiceCatalogUrl`, () => {
      it(`replaces current url with url from the service catalog`, () => {
        return assert.becomes(device.useServiceCatalogUrl(`https://conv-a.wbx2.com/conversation/v1`), `https://conv-a4.wbx2.com/conversation/v1`);
      });
    });

    describe(`#clear()`, () => {
      it(`does not remove \`logger\``, () => {
        assert.property(device, `logger`);
        assert.isDefined(device.logger);
        device.clear();
        assert.property(device, `logger`);
        assert.isDefined(device.logger);
      });

      it(`clears all features`, () => {
        assert.isAbove(device.features.developer.length, 0);
        device.clear();
        assert.lengthOf(device.features.developer, 0);
      });
    });

    describe(`#determineService()`, () => {
      it(`determines the service backing the specified url`, () => assert.becomes(device.determineService(`https://conv-a.wbx2.com/conversation/api/v1/conversations`), `conversation`));
      it(`rejects if no service can be determined`, () => assert.isRejected(device.determineService(`https://rogue-service.example.com`), /does not reflect a known service/));
    });

    describe(`#getPreDiscoveryServiceUrl()`, () => {
      it(`requires a \`service\` parameter`, () => {
        return assert.isRejected(device.getPreDiscoveryServiceUrl(), /`service` is a required parameter/);
      });

      it(`returns the specified hardcoded service url`, () => {
        device.config.preDiscoveryServices = {
          validServiceUrl: `http://example.com/pre-discovery-service`
        };

        return Promise.all([
          device.getPreDiscoveryServiceUrl(`invalid`)
            .then((url) => {
              assert.isUndefined(url);
            }),
          assert.becomes(device.getPreDiscoveryServiceUrl(`valid`), `http://example.com/pre-discovery-service`)
        ]);
      });
    });

    describe(`#getServiceUrl()`, () => {
      it(`requires a \`service\` parameter`, () => {
        return assert.isRejected(device.getServiceUrl(), /`service` is a required parameter/);
      });

      it(`returns the specified service url`, () => {
        device.services = {
          validServiceUrl: `http://example.com/valid-service`
        };

        return Promise.all([
          device.getServiceUrl(`invalid`)
            .then((url) => {
              assert.isUndefined(url);
            }),
          assert.becomes(device.getServiceUrl(`valid`), `http://example.com/valid-service`)
        ]);
      });

      describe(`when web-ha-messasing is enabled`, () => {
        it(`returns the service url with a different host if available`, () => {
          console.log(device.features.developer);
          device.features.developer.set([{
            key: `web-ha-messasing`,
            val: `true`,
            value: true,
            mutable: true,
            lastModified: `2015-06-29T20:02:48.033Z`
          }]);

          device.services = {
            validServiceUrl: `http://example.com/valid-service`,
            anotherServiceUrl: `http://another.com/another-service`
          };

          device.serviceHostMap = {
            hostCatalog: {
              'another.com': [
                {
                  host: `another-2.com`,
                  priority: 2
                },
                {
                  host: `another-1.com`,
                  priority: 1
                }
              ]
            }
          };
          return assert.becomes(device.getServiceUrl(`another`), `http://another-1.com/another-service`);
        });
      });

      describe(`when web-ha-messasing is disabled`, () => {
        it(`disregards the host catalog and returns the normal url`, () => {
          device.features.developer.set([{
            key: `web-ha-messasing`,
            val: `false`,
            value: false,
            mutable: true,
            lastModified: `2015-06-29T20:02:48.033Z`
          }]);

          device.services = {
            validServiceUrl: `http://example.com/valid-service`,
            anotherServiceUrl: `http://another.com/another-service`
          };

          device.serviceHostMap = {
            hostCatalog: {
              'another.com': [
                {
                  host: `another-1.com`,
                  priority: 1
                }
              ]
            }
          };

          return assert.becomes(device.getServiceUrl(`another`), `http://another.com/another-service`);
        });
      });

    });

    describe(`#markUrlFailedAndGetNew()`, () => {
      it(`requires a \`url\` parameter`, () => {
        return assert.isRejected(device.markUrlFailedAndGetNew(), /`url` is a required parameter/);
      });

      it(`returns service url for the next available host`, () => {
        return assert.becomes(device.markUrlFailedAndGetNew(`https://mercury-connection.a6.ciscospark.com/v1`), `https://mercury-connection-a5.wbx2.com/v1`);
      });

      it(`calls refresh to get new data if it cycled through all the hosts`, () => {
        sinon.spy(device, `refresh`);
        assert.notCalled(device.refresh);
        device.services = {
          failingServiceUrl: `http://fail.com/v1`
        };
        device.serviceHostMap = {
          hostCatalog: {
            'fail.com': [
              {
                host: `fail-2.com`,
                priority: 2
              },
              {
                host: `fail-1.com`,
                priority: 1
              }
            ]
          }
        };

        const request = device.spark.request;
        assert.isTrue(device.registered);
        request.onCall(0).returns(Promise.resolve({
          body: {
            services: {
              failingServiceUrl: `http://fail.com/v1`
            },
            serviceHostMap: {
              hostCatalog: {
                'fail.com': [
                  {
                    host: `new-4.com`,
                    priority: 2
                  },
                  {
                    host: `new-3.com`,
                    priority: 1
                  }
                ]
              }
            }
          }
        }));

        return assert.becomes(device.markUrlFailedAndGetNew(`http://fail-1.com/v1`), `http://fail-2.com/v1`)
          .then(() => assert.becomes(device.markUrlFailedAndGetNew(`http://fail-2.com/v1`), `http://new-3.com/v1`))
          .then(() => {
            assert.calledOnce(device.refresh);
          });
      });
    });

    describe(`#isPreDiscoveryService()`, () => {
      it(`requires a \`service\` parameter`, () => {
        return assert.isRejected(device.isPreDiscoveryService(), /`service` is a required parameter/);
      });

      it(`indicates whether the specified service is one of the hardcoded app services`, () => {
        device.config.preDiscoveryServices = {
          validServiceUrl: `http://example.com/pre-discovery-service`
        };

        return Promise.all([
          assert.becomes(device.isPreDiscoveryService(`invalid`), false),
          assert.becomes(device.isPreDiscoveryService(`valid`), true)
        ]);
      });
    });

    describe(`#isPreDiscoveryServiceUrl()`, () => {
      it(`requires a \`url\` parameter`, () => {
        return assert.isRejected(device.isPreDiscoveryServiceUrl(), /`uri` is a required parameter/);
      });

      it(`indicates whether the specified url is one of the hardcoded app services`, () => {
        device.config.preDiscoveryServices = {
          validServiceUrl: `http://example.com/pre-discovery-service`
        };

        return Promise.all([
          assert.becomes(device.isPreDiscoveryServiceUrl(`http://example.com/disovered-service/some-endpoint`), false),
          assert.becomes(device.isPreDiscoveryServiceUrl(`http://example.com/pre-discovery-service/some-endpoint`), true)
        ]);
      });
    });

    describe(`#isService()`, () => {
      it(`requires a \`service\` parameter`, () => {
        return assert.isRejected(device.isService(), /`service` is a required parameter/);
      });

      it(`indicates whether the specified service is known to the device`, () => {
        device.services = {
          validServiceUrl: `http://example.com/valid-service`
        };
        return Promise.all([
          assert.becomes(device.isService(`invalid`), false),
          assert.becomes(device.isService(`valid`), true)
        ]);
      });
    });

    describe(`#isSpecificService()`, () => {
      it(`resolves with true if the service and key match`, () => assert.becomes(device.isSpecificService(`wdm`, `wdm`), true));
      it(`resolves with true if the service is a url that matches the service specified by key`, () => assert.becomes(device.isSpecificService(`conversation`, `https://conv-a.wbx2.com/conversation/api/v1/conversations`), true));
      it(`resolves with false if the service does not match the key`, () => assert.becomes(device.isSpecificService(`wdm`, `https://conv-a.wbx2.com/conversation/api/v1/conversations`), false));
    });

    describe(`#isServiceUrl()`, () => {
      it(`requires a \`url\` parameter`, () => {
        return assert.isRejected(device.isServiceUrl(), /`uri` is a required parameter/);
      });

      it(`indicates whether the specified url is for a service known to the device`, () => {
        device.services = {
          validServiceUrl: `http://example.com/service`
        };
        return Promise.all([
          assert.becomes(device.isServiceUrl(`http://example.com/not-a-service/some-endpoint`), false),
          assert.becomes(device.isServiceUrl(`http://example.com/service/some-endpoint`), true)
        ]);
      });
    });

    describe(`#register()`, () => {
      describe(`when the device is already registered`, () => {
        it(`refreshes the device`, () => {
          sinon.spy(device, `refresh`);

          device.url = `http://example.com/device/1`;
          assert.isTrue(device.registered);

          assert.notCalled(device.refresh);
          return device.register()
            .then(() => assert.calledOnce(device.refresh));
        });
      });
    });

    describe(`#refresh()`, () => {
      describe(`when the device is not registered`, () => {
        it(`registers the device`, () => {
          sinon.spy(device, `register`);

          device.unset(`url`);


          assert.notCalled(device.register);
          return device.refresh()
            .then(() => assert.calledOnce(device.register));
        });
      });

      describe(`when the service responds with a 404`, () => {
        it(`registers the device`, () => {
          const newUrl = `http://example.com/device/2`;
          const request = device.spark.request;
          assert.isTrue(device.registered);
          request.onCall(0).returns(Promise.reject({statusCode: 404}));
          request.onCall(1).returns(Promise.resolve({body: {url: newUrl}}));
          return device.refresh()
            .then(() => {
              assert.equal(device.url, newUrl);
              assert.calledTwice(request);
            });
        });
      });
    });

    describe(`#serialize()`, () => {
      it(`serializes feature toggles in a format compatible with WDM`, () => {
        assert.deepEqual(device.serialize().features, deviceFixture.features);
      });
    });
  });
});
