import {assert} from '@webex/test-helper-chai';
import sinon from '@webex/test-helper-sinon';
import MockSpark from '@webex/test-helper-mock-webex';
import Lyra, {config as lyraConfig} from '@webex/internal-plugin-lyra';

describe('plugin-lyra', () => {
  const lyraSpaceId = 'lyra-1337';
  const lyraSpaceUrl = `https://lyra/api/v1/${lyraSpaceId}`;
  const lyraSpace = {
    identity: {
      id: lyraSpaceId
    },
    url: lyraSpaceUrl
  };

  let spark;

  before(() => {
    spark = new MockSpark({
      children: {
        lyra: Lyra
      },
      device: {
        url: 'deviceUrl',
        userId: '1234'
      }
    });

    spark.config.lyra = lyraConfig.lyra;
  });

  beforeEach(() => {
    spark.request.resetHistory();
  });

  describe('device', () => {
    describe('#putAudioState', () => {
      it('requires audioState.deviceUrl', () => assert.isRejected(spark.internal.lyra.device.putAudioState(lyraSpace), /audioState.deviceUrl is required/));
    });

    describe('#setVolume', () => {
      it('defaults to level 0', () => spark.internal.lyra.device.setVolume(lyraSpace)
        .then(() => {
          assert.calledWith(spark.request, sinon.match({
            method: 'POST',
            uri: `${lyraSpace.url}/audio/volume/actions/set/invoke`,
            body: {
              level: 0
            }
          }));
        }));
    });
  });
});
