/**
 *
 * Mimic browser APIs as "the ultimate browser".
 * Handles the quirks of each browser.
 * Extends and enhances adapter.js, i.e., the "media" file from the web client.
 * @export
 * @class Media
 */
export default class Media {
  /**
   * Create an instance of Media.
   * @param {object} devices
   * @param {object} streams
   * @memberof Media
   */
  constructor(devices, streams) {
    this.devices = devices;
    this.streams = streams;
  }

  /**
   *
   * Select which user device stream to use as media input
   * @returns {null}
   * @memberof Media
   */
  getUserMedia() {}

  /**
   *
   * Get supported media inputs?
   * @returns {null}
   * @memberof Media
   */
  getSupportedMedia() {}

  /**
   *
   * Capture screen sharing stream
   * @returns {null}
   * @memberof Media
   */
  getScreenShare() {}

  /**
   *
   * Toggle a specific stream
   * @returns {null}
   * @memberof Media
   */
  toggleStream() {}

  /**
   *
   * Stop input stream
   * @returns {null}
   * @memberof Media
   */
  stopStream() {}
}
