/**
 *
 * Media Health Checker
 * @export
 * @class MediaChecker
 */
const MediaChecker = {
  /**
   * Create an instance of MediaChecker.
   * @param {object} options
   * @memberof MediaChecker
   */
  constructor(options) {
    this.isAudioSent = options.isAudioSent;
  },

  /**
   *
   * Cisco-specific stuff Get raw info and perform analysis with stats.
   * @returns {object}
   * @memberof MediaChecker
   */
  getAnalysis() {},

  /**
   *
   * Is video being rendered
   * @returns {bool}
   * @memberof MediaChecker
   */
  isVideoRendered() {},

  /**
   *
   * Is media being sent
   * @returns {bool}
   * @memberof MediaChecker
   */
  isMediaSending() {},

  /**
   *
   * Is media quality good enough to keep using
   * @returns {bool}
   * @memberof MediaChecker
   */
  isMediaQualityGood() {},

  /**
   *
   * Is audio being sent
   * @returns {bool}
   * @memberof MediaChecker
   */
  isAudioSent() {
    return this.isAudioSent;
  }
};

export default MediaChecker;
