import '@webex/internal-plugin-conversation';
import '@webex/plugin-phone';

import {assert} from '@webex/test-helper-chai';
import WebexCore from '@webex/webex-core';
import testUsers from '@webex/test-helper-test-users';
import {browserOnly, handleErrorEvent} from '@webex/test-helper-mocha';
import {base64} from '@webex/common';

import {
  expectActiveEvent,
  expectCallIncomingEvent
} from '../lib/event-expectations';

browserOnly(describe)('plugin-phone', function () {
  this.timeout(30000);

  describe('Call', () => {
    describe('with group calling enabled', () => {
      let mccoy, spock, bones, participants;

      beforeEach('create users and register', () => testUsers.create({count: 3})
        .then((users) => {
          [mccoy, spock, bones] = users;
          participants = [mccoy, spock, bones];
          spock.webex = new WebexCore({
            credentials: {
              authorization: spock.token
            },
            config: {
              phone: {
                enableExperimentalGroupCallingSupport: true
              }
            }
          });

          mccoy.webex = new WebexCore({
            credentials: {
              authorization: mccoy.token
            },
            config: {
              phone: {
                enableExperimentalGroupCallingSupport: true
              }
            }
          });

          return Promise.all([
            spock.webex.phone.register(),
            mccoy.webex.phone.register()
          ]);
        }));

      afterEach('unregister spock and mccoy', () => Promise.all([
        spock && spock.webex.phone.deregister()
          // eslint-disable-next-line no-console
          .catch((reason) => console.warn('could not disconnect spock from mercury', reason)),
        mccoy && mccoy.webex.phone.deregister()
          // eslint-disable-next-line no-console
          .catch((reason) => console.warn('could not disconnect mccoy from mercury', reason))
      ]));

      describe('#from', () => {
        describe('1:1 spaces', () => {
          it('represents the initiating party on a 1:1', () => handleErrorEvent(spock.webex.phone.dial(mccoy.email), (call) => Promise.all([
            expectCallIncomingEvent(mccoy.webex.phone)
              .then((c) => c.answer()),
            expectActiveEvent(call)
              .then(() => {
                assert.property(call.from, 'isInitiator');
                assert.property(call.from, 'personId');
                assert.property(call.from, 'state');
                assert.isHydraID(call.from.personId);
                assert.isTrue(call.from.isInitiator);
                assert.equal(base64.decode(call.from.personId).split('/').pop(), spock.id);
              })
          ])));
        });

        describe('space calling', () => {
          let conversationUrl;

          beforeEach('create group space', () => spock.webex.internal.conversation.create({participants}).then((c) => {
            conversationUrl = base64.encode(`ciscospark://us/ROOM/${c.id}`);
          }));

          it('is undefined for a group call', () => handleErrorEvent(spock.webex.phone.dial(conversationUrl), (call) => Promise.all([
            expectCallIncomingEvent(mccoy.webex.phone)
              .then((c) => {
                mccoy.webex.logger.info(c);
                c.answer();
              }),
            expectActiveEvent(call)
              .then(() => {
                assert.isUndefined(call.from);
              })
          ])));
        });
      });

      describe('#host', () => {
        describe('1:1 spaces', () => {
          it('represents the initiating party on a 1:1', () => handleErrorEvent(spock.webex.phone.dial(mccoy.email), (call) => Promise.all([
            expectCallIncomingEvent(mccoy.webex.phone)
              .then((c) => c.answer()),
            expectActiveEvent(call)
              .then(() => {
                assert.property(call.host, 'id');
                assert.property(call.host, 'name');
                assert.equal(call.host.id, spock.id);
              })
          ])));
        });

        describe('space calling', () => {
          let conversationUrl;

          beforeEach('create group space', () => spock.webex.internal.conversation.create({participants}).then((c) => {
            conversationUrl = base64.encode(`ciscospark://us/ROOM/${c.id}`);
          }));

          it('is initiator of the group call', () => handleErrorEvent(spock.webex.phone.dial(conversationUrl), (call) => Promise.all([
            expectCallIncomingEvent(mccoy.webex.phone)
              .then((c) => {
                c.answer();
              }),
            expectActiveEvent(call)
              .then(() => {
                assert.property(call.host, 'id');
                assert.property(call.host, 'name');
                assert.equal(call.host.id, spock.id);
              })
          ])));
        });
      });

      describe('#roomId', () => {
        describe('1:1 spaces', () => {
          it('represents the initiating party on a 1:1', () => handleErrorEvent(spock.webex.phone.dial(mccoy.email), (call) => Promise.all([
            expectCallIncomingEvent(mccoy.webex.phone)
              .then((c) => c.answer()),
            expectActiveEvent(call)
              .then(() => {
                assert.property(call, 'roomId');
                assert.isHydraID(call.roomId);
              })
          ])));
        });

        describe('space calling', () => {
          let conversationUrl;

          beforeEach('create group space', () => spock.webex.internal.conversation.create({participants}).then((c) => {
            conversationUrl = base64.encode(`ciscospark://us/ROOM/${c.id}`);
          }));

          it('is initiator of the group call', () => handleErrorEvent(spock.webex.phone.dial(conversationUrl), (call) => Promise.all([
            expectCallIncomingEvent(mccoy.webex.phone)
              .then((c) => {
                c.answer();
              }),
            expectActiveEvent(call)
              .then(() => {
                assert.property(call, 'roomId');
                assert.isHydraID(call.roomId);
                assert.equal(call.roomId, conversationUrl);
              })
          ])));
        });
      });
    });
  });
});
