import testUsers from '@webex/test-helper-test-users';
import {expect} from 'chai';

describe('samples', () => {
  describe('browser-call-with-screenshare', () => {
    describe('normal dialing', () => {
      let mccoy, spock;

      const browserSpock = browser.select('browserSpock');
      const browserMccoy = browser.select('browserMccoy');

      before('create test users', () => testUsers.create({count: 2})
        .then((users) => {
          [spock, mccoy] = users;
          // Adding pause to let test users propagate through integration
          browser.pause(2500);
        }));

      it('pauses and waits for h264 to download', () => {
        browser.refresh();
        browserMccoy.pause(30000);
        browser.refresh();
        browserMccoy.pause(30000);
        browser.refresh();
      });

      it('loads the app', () => {
        browser.url('/browser-call-with-screenshare');
        browser.waitForExist('#access-token', 1500);
      });

      it('connects mccoy\'s browser', () => {
        expect(browserMccoy.getTitle()).to.equal('Sample: Local Screensharing');
        browserMccoy.execute((token) => {
          // eslint-disable-next-line no-undef
          document.querySelector('[placeholder="Your access token"]').value += token;
        }, mccoy.token.access_token);
        browserMccoy.click('#connect');
        browserMccoy.waitForExist('.listening');
      });

      it('connects spock\'s browser', () => {
        expect(browserSpock.getTitle()).to.equal('Sample: Local Screensharing');
        browserSpock.execute((token) => {
          // eslint-disable-next-line no-undef
          document.querySelector('[placeholder="Your access token"]').value += token;
        }, spock.token.access_token);
        browserSpock.click('#connect');
        browserSpock.waitForExist('.listening');
      });

      it('places call from spock to mccoy', () => {
        browserSpock.setValue('[placeholder="Person ID or Email Address or SIP URI or Room ID"]', mccoy.emailAddress);
        browserSpock.click('[title="dial"]');

        browserSpock.waitUntil(() => (browserSpock.getText('#call-status-local') === 'IN_MEETING'), 10000, 'Timed-out waiting for local user to connect to meeting');
      });

      it('joins the call on mccoy', () => {
        browserMccoy.waitUntil(() => {
          try {
            const alerttext = browserMccoy.alertText();

            return alerttext === 'Answer incoming call';
          }
          catch (error) {
            // Error is thrown when alert isn't open which is fine
            return false;
          }
        }, 10000, 'Timed out waiting for incoming call alert');
        browserMccoy.alertAccept();
        browserSpock.waitUntil(() => (browserSpock.getText('#call-status-remote') === 'IN_MEETING'), 10000, 'Timed-out waiting for remote user to connect to meeting');
      });

      it('starts screensharing', () => {
        browserSpock.click('button[title="share screen"]');

        browserSpock.waitUntil(() => (browserSpock.getText('#screenshare-tracks') === 'SHARING'), 10000, 'Timed-out waiting for screenshare to start');
      });

      it('stops screensharing', () => {
        // Pausing for screenshare to establish because we don't currently have stream stats displayed
        browser.pause(2500);
        browserSpock.click('button[title="stop screen share"]');
        browserSpock.waitUntil(() => (browserSpock.getText('#screenshare-tracks') === 'STOPPED'), 10000, 'Timed-out waiting for screenshare to stop');
      });

      it('starts screensharing remote', () => {
        browserMccoy.click('button[title="share screen"]');

        browserMccoy.waitUntil(() => (browserMccoy.getText('#screenshare-tracks') === 'SHARING'), 10000, 'Timed-out waiting for screenshare to start');
      });

      it('stops screensharing remote', () => {
        // Pausing for screenshare to establish because we don't currently have stream stats displayed
        browser.pause(2500);
        browserMccoy.click('button[title="stop screen share"]');
        browserMccoy.waitUntil(() => (browserMccoy.getText('#screenshare-tracks') === 'STOPPED'), 10000, 'Timed-out waiting for screenshare to stop');
      });

      it('ends the call', () => {
        browserSpock.click('[title="hangup"]');
        browserSpock.waitUntil(() => (browserSpock.getText('#call-status-local') === 'NOT_IN_MEETING'), 10000, 'Timed-out waiting for local user to disconnect from meeting');
      });
    });
  });
});
