  let deviceId;

  if (webex.devicemanager && webex.devicemanager._pairedDevice) {
    deviceId = webex.devicemanager._pairedDevice.identity.id;
  }
  const pin = document.getElementById('pin-join').value;
  const moveToResource = false;
