/* globals browser */
import {expect} from 'chai';
import {flaky} from '@webex/test-helper-mocha';
import testUsers from '@webex/test-helper-test-users';

describe('samples', () => {
  describe('browser-plugin-meetings', () => {
    describe('normal dialing', () => {
      let mccoy, spock;

      const browserFirefox = browser.select('browserFirefox');
      const browserChrome = browser.select('browserChrome');

      before('create test users', () => testUsers.create({count: 2})
        .then((users) => {
          [mccoy, spock] = users;

          // Adding pause to let test users propagate through integration
          browser.pause(2500);
        }));

      before('reload browser', () => {
        browser.refresh();
      });

      it('loads the app', () => {
        browser.url('/browser-plugin-meetings');
        browser.waitForExist('#access-token', 1500);
      });

      it('connects spock\'s browser', () => {
        expect(browserChrome.getTitle()).to.equal('Sample: Meetings');
        browserChrome.setValue('#access-token', spock.token.access_token);
        browserChrome.pause(10000);
        browserChrome.click('#connect');

        browserChrome.waitUntil(() => {
          // Test runs too quickly sometimes and tries to click before call is fully established
          if (browserChrome.getText('#connection-status') !== 'connected') {
            return false;
          }

          return true;
        }, 10000, 'Timed-out waiting for login');
      });

      it('connects mccoy\'s browser', () => {
        expect(browserFirefox.getTitle()).to.equal('Sample: Meetings');
        browserFirefox.setValue('#access-token', mccoy.token.access_token);
        browserFirefox.pause(10000);
        browserFirefox.click('#connect');
        browserFirefox.waitUntil(() => {
          // Test runs too quickly sometimes and tries to click before call is fully established
          if (browserFirefox.getText('#connection-status') !== 'connected') {
            return false;
          }

          return true;
        }, 10000, 'Timed-out waiting for login');
      });

      it('places call from spock to mccoy', () => {
        browserFirefox.setValue('#invitee', mccoy.emailAddress);
        browserFirefox.click('[title="create"]');
        browserFirefox.pause(5000);
        browserFirefox.click('#join');
        browserFirefox.pause(5000);
        browserFirefox.click('#addMedia');
      });

      flaky(it, process.env.SKIP_FLAKY_TESTS)('mccoy answers the call', () => {
        browserChrome.pause(5000);
        browserChrome.waitForVisible('#answer', 20000);
        browserChrome.click('#answer');
        browserChrome.pause(5000);
        browserChrome.waitForVisible('#addMedia', 20000);
        browserChrome.click('#addMedia');
        browserChrome.waitForVisible('#remotevideo', 20000);
        browserChrome.waitForVisible('#stop-sending-audio', 20000);
        browserChrome.click('#stop-sending-audio');
        browserFirefox.waitForVisible('#stop-sending-audio', 20000);
        browserFirefox.click('#stop-sending-audio');
        browserChrome.pause(160000);
      });

      it('ends the call', () => {
        browser.click('#leave');
      });
    });
  });
});
