import {assert} from '@webex/test-helper-chai';
import Webex from 'webex';
import {version} from 'webex/package';

describe('webex', () => {
  describe('Webex', () => {
    describe('.version', () => {
      it('exists', () => {
        assert.property(Webex, 'version');
        assert.equal(Webex.version, version);
      });
    });

    describe('#version', () => {
      it('exists', () => {
        const webex = new Webex();

        assert.property(webex, 'version');
        assert.equal(webex.version, version);
      });
    });
  });
});
